package Day01;

public class _12_doWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1. dowhile 반복문
		
		int i = 0;
		
		//조건식을 타기전에 do 구문이 먼저 실행됨
		//조건식에서 while 문이 true이기에 do 구문 한줄만 찍히게 됨
		do {
			System.out.println("hello java");
		} while(i > 100);
	}

}
